import axios from "axios"

export const getAllUsers = async (token = '') => {
    try {
        const { data,status } = await axios.get('/api/v1/user', { headers: { 'x-access-tokens': token } });
        return status ? data : [];
    } catch (err) {
        console.error(err);
        return []
    }
}
