import { IonButton, IonProgressBar } from "@ionic/react"
import {  Form } from 'react-bootstrap';
import './styles.css';

import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { chekSesion } from "../../redux/actions/sesion.actions";
import LayoutLogin from "../../components/Layout/LayoutLogin";
import FormLogin from "./components/FormLogin";

type formType = {
    email: string,
    password: string,
    statusPass: boolean,
    statusRes:boolean,
    statusAlert: boolean,
    messageAlert: string,
    loading:boolean
}
const initialStateForm: formType = {
    email: '',
    password: '',
    statusPass: false,
    statusRes:false,
    statusAlert: false,
    messageAlert: 'este es un mensaje',
    loading:false
}

const Login = () => {
    const [state, setState] = useState<formType>(initialStateForm);
    const dispatch = useDispatch();

    const submitLogin = (e:any) => {
        e.preventDefault();
        dispatch(chekSesion(state));
    }

    return <LayoutLogin>
        <Form onSubmit={submitLogin}>
            <FormLogin
                title={'Correo'}
                required={true}
                minLength={8}
                type='email'
                placeholder='usuario@dominio'
                value={state.email}
                onChange={(e:any) => setState({ ...state, email: e.target.value })}
            />

            <FormLogin
                title={'Contrase単a'}
                required
                type={state.statusPass ?'':'password'}
                placeholder='contrase単a'
                minLength={6}
                value={state.password}
                onChange={(e:any) => setState({ ...state, password: e.target.value })}
            />

            <Form.Check >
                <Form.Check.Input
                    checked={state.statusPass}
                    onChange={() => setState({ ...state, statusPass: !state.statusPass })}
                />
                <Form.Check.Label>mostrar contrase単a</Form.Check.Label>
            </Form.Check>

            <br/>
            <IonButton disabled={state.loading} fill='solid' type='submit' expand='block' >
                { state.loading ? <IonProgressBar value={0.25} buffer={0.5} type="indeterminate" /> :'Entrar'}
            </IonButton>

        </Form>
    </LayoutLogin>
}

export default Login;
