import { getAllUsers } from "../../api/users.api"
import { loaddingOff, loaddingOn, menssageOn } from "./effects.actions";


export const optenerUsuarios = (token='') => {

    return async (dispatch: Function) => {
        dispatch(loaddingOn());
        dispatch({ type: 'REMOVE_USUARIOS' });
        try {
            const value = await getAllUsers(token);
            dispatch({ type: 'LISTA_USUARIOS', value });
            dispatch(menssageOn({
                status: true,
                text: `Total de usuarios descargados ${value.length}...`,
                title: '',
                type: 'info'// : 'danger'
            }));
        } catch(error) {
            dispatch(menssageOn({
                status: true,
                text: `Error al descargar usuarios!!!`,
                title: '',
                type: 'danger'
            }));
        }
        dispatch(loaddingOff());
    }
}
