import { IonButton, IonCard, IonCardContent, IonCardHeader, IonCardTitle, IonContent, IonImg, IonPage, IonProgressBar } from "@ionic/react"
import { FormControl, Form } from 'react-bootstrap';
import './styles.css';

import imagen from './assets/mar_1.jpg';
import { useState } from "react";
import { useDispatch } from "react-redux";
import { chekSesion } from "../../redux/actions/sesion.actions";
import LayoutLogin from "../../components/Layout/LayoutLogin";

type formType = {
    email: string,
    password: string,
    statusPass: boolean,
    statusRes:boolean,
    statusAlert: boolean,
    messageAlert: string,
    loading:boolean
}
const initialStateForm: formType = {
    email: '',
    password: '',
    statusPass: false,
    statusRes:false,
    statusAlert: false,
    messageAlert: 'este es un mensaje',
    loading:false
}

const Login = () => {
    const [state, setState] = useState<formType>(initialStateForm);
    const dispatch = useDispatch();

    const submitLogin = (e:any) => {
        e.preventDefault();
        dispatch(chekSesion(state));
    }

    return <LayoutLogin>
        <Form onSubmit={submitLogin}>
            <Form.Group>
                <Form.Label>Correo</Form.Label>
                <FormControl
                    required
                    minLength={8}
                    type='email'
                    placeholder='usuario@dominio'
                    value={state.email}
                    onChange={e => setState({ ...state, email: e.target.value })}
                />
        </Form.Group>

        <Form.Group>
            <Form.Label>Contraseña</Form.Label>
            <FormControl
                required
                type={state.statusPass ?'':'password'}
                placeholder='contraseña'
                minLength={6}
                value={state.password}
                onChange={e => setState({ ...state, password: e.target.value })}
            />
        </Form.Group>

                            <Form.Check >
                                <Form.Check.Input
                                    checked={state.statusPass}
                                    onChange={() => setState({ ...state, statusPass: !state.statusPass })}
                                />
                                <Form.Check.Label>mostrar contraseña</Form.Check.Label>
                            </Form.Check>

                            <br/>
                            <IonButton disabled={state.loading} fill='solid' type='submit' expand='block' >
                                { state.loading ? <IonProgressBar value={0.25} buffer={0.5} type="indeterminate" /> :'Entrar'}
                            </IonButton>

                        </Form>
                  </LayoutLogin>
}

export default Login;
