import axios from "axios"

export const getAllUsers = async (token = '') => {
    try {
        const { data, status } = await axios.get('/api/v1/user', );        
        return status===200 ? data : [];
    } catch (err) {
        console.error(err);
        return []
    }
}
export const getAUserId = async (token = '',id='') => {
    try {
        const { data, status } = await axios.get(`/api/v1/user/${id}`, { headers: { 'x-access-tokens': token } });        
        return status===200 ? {...data.user,puesto:data.data.puesto} : null;
    } catch (err) {
        console.error(err);
        return null
    }
}
